#include <stdio.h>
#include <stdlib.h>
#include <string.h>

#include <cryptolens.h>

int
main() {
  cryptolens_error_t e;
  cryptolens_reset_error(&e);
  cryptolens_signature_verifier_t* signature_verifier = cryptolens_SV_init(&e);

  cryptolens_SV_set_modulus_base64(&e, signature_verifier, "");
  cryptolens_SV_set_exponent_base64(&e, signature_verifier, "AQAB");

  char const* json_string = "{\"licenseKey\":\"\",\"signature\":\"\",\"result\":0,\"message\":\"\"}";
  cryptolens_handle_activate_response(&e, signature_verifier, json_string);

  printf("%s\n", !cryptolens_check_error(&e) ? "Signature check was successful!" : "Signature check failed!");

  cryptolens_SV_destroy(signature_verifier);
}
